def read_input() -> list[Move]:
    return [read_move(c) for c in INPUT.strip()]


def main():
    moves = read_input()

    game = Game(ROCK_SHAPES, Board(BOARD_WIDTH))
    move_seq = itertools.cycle(moves)
    play_game(
        game,
        moves=move_seq,
        rock_count=2022,
    )
    print(f'Final height: {game.board.height}')


if __name__ == '__main__':
    main()
